<script lang="ts">
  import { utils } from 'animejs'
  import { Spring } from 'svelte/motion'

  import { DEFAULT_SPRING_CONFIG } from '@/lib/animations/constants'
  import { gestures } from '@/lib/svelte/gestures.svelte'
  import { centerPointOrigin } from '@/lib/utils/math'

  interface Props {
    isIn: boolean
  }

  const { isIn }: Props = $props()

  const spring = new Spring(
    {
      x: 0,
      y: 0,
    },
    DEFAULT_SPRING_CONFIG,
  )

  let face = $state<SVGElement>()
  let mouth = $state<SVGElement>()
  let eyeLeft = $state<SVGElement>()
  let eyeRight = $state<SVGElement>()
  let eyeBrownLeft = $state<SVGElement>()
  let eyeBrownRight = $state<SVGElement>()

  const faceTransform = $derived.by(() => {
    if (!face) return ''

    const bound = face.getBoundingClientRect()

    const [centerX, centerY] = centerPointOrigin(
      [bound.x, bound.y],
      [Math.floor(bound.width / 2), Math.floor(bound.height / 2)],
    )

    const x = utils.clamp((spring.current.x - centerX) * 0.005 + 5, 0, 10)
    const y = utils.clamp((spring.current.y - centerY) * 0.005, -5, 5)

    return `translateX(${x}px) translateY(${y}px) rotateY(${x}deg) rotateZ(${y * 0.3}deg) scale(0.99)`
  })

  const mouthPath = $derived.by(() => {
    if (!mouth) return ''

    const bound = mouth.getBoundingClientRect()

    const [centerX, centerY] = centerPointOrigin(
      [bound.x, bound.y],
      [Math.floor(bound.width / 2), Math.floor(bound.height / 2)],
    )

    const x = utils.clamp((spring.current.x - centerX) * 0.002, -5, 5)
    const y = utils.clamp((spring.current.y - centerY) * 0.002, -5, 5)

    return `M${558 + Math.min(x, 0)},${525 + Math.min(x, 0) * y} C598,${538 + Math.min(x, 0) * -y} 636,${546 + Math.max(x, 0) * y} ${671 + Math.max(x, 0)},${525 + Math.max(x, 0) * -y}`
  })

  const eyeRightTransform = $derived.by(() => {
    if (!eyeRight) return ''

    const bound = eyeRight.getBoundingClientRect()

    const [centerX, centerY] = centerPointOrigin(
      [bound.x, bound.y],
      [Math.floor(bound.width / 2), Math.floor(bound.height / 2)],
    )

    const x = utils.clamp((spring.current.x - centerX) * 0.015, -12, 12)
    const y = utils.clamp((spring.current.y - centerY) * 0.01, -5, 10)
    const scale = utils.clamp(Math.abs(x * y) * 0.002, 0, 0.3)

    return `translateX(${x}px) translateY(${y}px) scale(${1 - scale})`
  })

  const eyeLeftTransform = $derived.by(() => {
    if (!eyeLeft) return ''

    const bound = eyeLeft.getBoundingClientRect()

    const [centerX, centerY] = centerPointOrigin(
      [bound.x, bound.y],
      [Math.floor(bound.width / 2), Math.floor(bound.height / 2)],
    )

    const x = utils.clamp((spring.current.x - centerX) * 0.01, -10, 10)
    const y = utils.clamp((spring.current.y - centerY) * 0.005, -10, 10)
    const scale = utils.clamp(Math.abs(x * y) * 0.001, 0, 0.3)

    return `translateX(${x}px) translateY(${y}px) scale(${1 - scale})`
  })

  const [eyeBrownLeftTransform, eyeBrownRightTransform] = $derived.by(() => {
    if (!eyeBrownLeft || !eyeBrownRight) return ['', '']

    const boundLeft = eyeBrownLeft.getBoundingClientRect()
    const boundRight = eyeBrownRight.getBoundingClientRect()

    const [centerLeftX, centerLeftY] = centerPointOrigin(
      [boundLeft.x, boundLeft.y],
      [Math.floor(boundLeft.width / 2), Math.floor(boundLeft.height / 2)],
    )
    const [centerRightX, centerRightY] = centerPointOrigin(
      [boundRight.x, boundRight.y],
      [Math.floor(boundRight.width / 2), Math.floor(boundRight.height / 2)],
    )

    const rawLeftX = spring.current.x - centerLeftX
    const rawRightX = spring.current.x - centerRightX

    let leftY = utils.clamp((spring.current.y - centerLeftY) * 0.1, -10, 5)
    let rightY = utils.clamp((spring.current.y - centerRightY) * 0.1, -10, 5)

    if (Math.sign(rawLeftX) === Math.sign(rawRightX)) {
      if (Math.abs(rawLeftX) > Math.abs(rawRightX)) {
        leftY = 0
      } else {
        rightY = 0
      }
    }

    return [`translateY(${leftY}px)`, `translateY(${rightY}px)`]
  })

  $effect.pre(() => {
    spring.set(
      { x: innerWidth / 2, y: innerHeight / 2 },
      {
        instant: true,
      },
    )
  })
</script>

<svelte:window
  {@attach gestures({
    onMove: ({ xy: [x, y] }) => {
      if (!isIn) {
        return
      }
      spring.set({ x, y })
    },
  })}
/>

